import { Stands } from './Stands';




export const Stations = ({station}) =>{
const [active, setActive] = useState([])

let ItemList = []

const toggle = (index) => {
  // a[index] = (a[index] === '' ? 'close' : '')
  // setActive(active[index])

}

// const setingArray = () =>{
//   setActive([...active, 'close'])
// }

if(station){

   ItemList = station.map((sta, i) => {

    return (
      <div key={sta.id} className='station-card'>
      {
        active
      }
      <button className='accordion' onClick={toggle(i)} ></button>
        <p className={active[i]}>
          <h3>{sta.name}</h3>
          <Stands station={sta}/>
        </p>
      </div>


    )
  })
}

  return(
<div className='station-wraper'>
  {ItemList}
</div>
  )
}
