import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { AreaChart, Area, Tooltip, CartesianGrid, XAxis, YAxis } from 'recharts';

export const MeasuringData = ({standId}) =>{

const [data, setData] = useState([])


useEffect(() => {

  axios.get(`/measuring-data/${standId}`)
  .then((res) => {
    setData(res.data.slice(1, 20));
  })
}, []);

console.log(data)

const formatXAxis = (tickItem) => {
  return tickItem.split(/T.*/,1);
}

  return(

    <AreaChart width={600} height={300} data={data} className='chart-wrapper' margin={{
          top: 10, right: 30, left: 0, bottom: 0,
        }}>
      <Area type="monotone" dataKey="value" stroke="#8884d8" fill="#8884d8" />
      <Tooltip />
      <CartesianGrid stroke="#ccc" strokeDasharray="1 1" />
      <XAxis dataKey="date" interval={0} angle={30} dx={30} tickFormatter={formatXAxis}/>
      <YAxis dataKey="value" />
    </AreaChart>


  )
}
