import React, { useState } from 'react';

export const Colapse = ({buttonName}) =>{

  const [active, setActive] = useState('close')

       const toggle = () =>{
           setActive(active === '' ? 'close' : '')
       }

  return(
    <div>
<button className='accordion' onClick={toggle} >{buttonName}</button>
<span className={active}>

  </span>
  </div>
  )
}
