import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../App.css';
import { Map, Marker, Popup, TileLayer } from 'react-leaflet'
import { Icon } from 'leaflet'
// import Icon from '../Icons/icons8-maple-leaf-30.png';

function PositionMap() {

  useEffect(() => {
    axios.get(`/stations`)
    .then((res) => {
      setStations(res.data);
    })
  }, []);
  const [stations, setStations] = useState([]);

  const leafPoint = new Icon({
    iconUrl: 'icons8-fallen-leaf-48.png',
    iconSize: [30, 30]

  });

  const position = [51.919437, 19.145136]

  return (

      <div className='map-wraper'>
      <Map center={position} zoom={6}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors"
        />

        {stations.map(sta => {
        return (
          <Marker key={sta.id} position={[sta.gegrLat, sta.gegrLon]} icon={leafPoint}>
            <Popup>{sta.name}<br />Easily customizable.</Popup>
          </Marker>
        )
      })}

      </Map>
      </div>


  )

}
export default PositionMap;
