import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { AreaChart, Area, Tooltip, CartesianGrid, XAxis, YAxis, ReferenceLine } from 'recharts';

export const MeasuringData = ({stand}) =>{

const [data, setData] = useState([])


useEffect(() => {

  axios.get(`/measuring-data/${stand.id}`)
  .then((res) => {
    setData(res.data.slice(14, 39));
  })

  axios.get(`/quality-index/${stand.index_id}`)
  .then((res) => {
    setData(res.data);
  })
}, []);

console.log(data)

const formatXAxis = (tickItem) => {
  return tickItem.split('T', 2)[1];
}

  return(

    <AreaChart width={750} height={300} data={data} className='chart-wrapper' margin={{
          top: 10, right: 20, left: 10, bottom: 0,
        }}>
      <Area type="monotone" dataKey="value" stroke="#8884d8" fill="#8884d8" />
      <CartesianGrid stroke="#ccc" strokeDasharray="1 1" />
      <XAxis height={100} dataKey="date" interval={0} angle={30} label="Time[h]" tickFormatter={formatXAxis}/>
       <ReferenceLine y={stand.index} label="Max" stroke="red" strokeDasharray="3 3" />
      <YAxis dataKey="value" label={{ value: stand.code + '[ug/m3]', angle: -90 }} width={110}/>
        <Tooltip />
    </AreaChart>


  )
}
