import React, { useState } from 'react';
import axios from 'axios';

import '../App.css';

function Search() {


    const [city, setCity] = useState('')
    const [stations, setStations] = useState('')

    const handlerCityChange = (event) => {
      setCity(event.target.value)
    }

    const handleSubmit = () => {

      axios.get(`/cities-stations/${city}`).then(({ data} ) => setStations(data['name']) );



}

    return (

      <div className='form-wraper'>
      <form onSubmit={handleSubmit} className='search-form'>
          <input className='search-field' value={city} name='search-field' placeholder='Search' type='text' onChange={handlerCityChange} />
        <button type='submit'>Search</button>
        {stations}
      </form>
      </div>

    )

}

export default Search;
