import React from 'react';
import '../App.css';
import { Map, Marker, Popup, TileLayer, Tooltip } from 'react-leaflet';
import { Icon } from 'leaflet';
import { Stations } from './Stations'


export const PositionMap = ({stations, position}) =>{


  const leafPoint = new Icon({
    iconUrl: 'icons8-fallen-leaf-48.png',
    iconSize: [30, 30]

  });



  return (
      <div className='map-wraper'>
      <Map center={position} zoom={6}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors"
        />
        {stations.map(sta => {
        return (
          <Marker key={sta.id} position={[sta.gegrLat, sta.gegrLon]} icon={leafPoint}
            onClick = <Stations/>
           >
            <Popup>{sta.name}<br />Easily customizable.</Popup>
            <Tooltip>
            </Tooltip>
          </Marker>

        )
      })}
      </Map>
      </div>
  )
}
export default PositionMap;
