import React, { useState } from 'react';
import '../App.css';
import SearchForm from './SearchForm';
import { Stations } from './Stations';
import axios from 'axios';




function MainPollinationGraphs() {

const [stationsName, setStationsName] = useState([]);
const [indexOfStation, setIndexOfStyation] = useState([]);

  const getSerchResult = (e) => {
    e.preventDefault();
  const result = e.target.elements.search.value;

    axios.get(`/cities-stations/${result}`)
    .then((res) => {
      setStationsName(res.data);
      setIndexOfStyation(Array(res.data.length).fill('close'))
      return(
         <Stations station = {stationsName} arrayOfLength = {indexOfStation}/ >
      )

    })
  }


  return (
    <div>
      <SearchForm getSerchResult = {getSerchResult} />

      </div>
  );
}
export default MainPollinationGraphs;
