import React, { Component } from 'react';
import '../App.css';
import { Map, Marker, Popup, TileLayer, Tooltip } from 'react-leaflet';
import { Icon } from 'leaflet';
import { Stations } from './Stations'


class PositionMap extends Component {
  constructor(props){
    super(props);
    this.stations = props.stations;
    this.position = props.position;
    this.leafPoint = new Icon({
      iconUrl: 'icons8-fallen-leaf-48.png',
      iconSize: [30, 30]

    });
  }

  render() {return(
      <div className='map-wraper'>
      <Map center={this.position} zoom={6}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors"
        />
        {this.stations.map(sta => {
        return (
          <Marker key={sta.id} position={[sta.gegrLat, sta.gegrLon]} icon={this.leafPoint} >
            <Popup>{sta.name}</Popup>
            <Tooltip>
              {sta.name}
            </Tooltip>
          </Marker>
        )
      })}
      </Map>
      </div>
  )}
}
export default PositionMap;
