import React, { useState, useEffect } from 'react';
import { Stands } from './Stands';




export const Stations = ({station, arrayOfLength}) =>{

const [active, setActive] = useState(arrayOfLength)
let ItemList = []

const toggle = (index) => {
  arrayOfLength[index] = arrayOfLength[index] === '' ? 'close' : ''
}



  console.log({active})

if(station){

   ItemList = station.map((sta, i) => {

    return (
      <div key={sta.id} className='station-card' >
      <button className='accordion' onClick={toggle(i)} ></button>
        <p className={arrayOfLength[i]}>
          <h3>{sta.name}</h3>
          <Stands station={sta}/>
        </p>
      </div>


    )
  })
}

  return(
<div className='station-wraper'>
  {ItemList}
</div>
  )
}
