import React, {useState} from 'react';
import { Stands } from './Stands';
export const Stations = ({station}) =>{


let ItemList = []


const [active, setActive] = useState('close')

console.log({active})

if(station){
   ItemList = station.map(sta => {

     const toggle = (event) =>{
         setActive(active === '' ? 'close' : '')
     }
    return (

      <div key={sta.id} className='station-card' >
      <button onClick={toggle} className='accordion'> {sta.name} </button>
        <span className={active}>
          <h3>{sta.name}</h3>
          <Stands station={sta}/>
        </span>
      </div>
    )
  })
}
  return(
<div className='station-wraper'>
  {ItemList}
</div>
  )
}
