import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {Line,ReferenceArea, LabelList,  Bar, BarChart, AreaChart, Area, Tooltip, CartesianGrid, XAxis, YAxis, ReferenceLine } from 'recharts';

export const MeasuringData = ({stand}) =>{

const [data, setData] = useState([])
const [quality, setQuality] = useState('')
const arrData = Array(5).fill(0);
let arrQuality = []

useEffect(() => {

  axios.get(`/measuring-data/${stand.id}`)
  .then((res) => {
    setData(res.data.slice(14, 39));
  })

  axios.get(`/quality-index/${stand.index_id}`)
  .then((res) => {
    setQuality(res.data);
  })
}, [stand.id, stand.index_id]);

  if(quality !== ''){
     arrQuality = [0, quality.very_good, quality.good, quality.moderate, quality.sufficient, quality.bad];
     arrData.forEach((item, i)=>{
       if(data.value - arrQuality[i+1]){
       arrData[i] = arrQuality[i+1]-arrQuality[i];
      }
     });
  }
  console.log(arrQuality)
  const formatXAxis = (tickItem) => {
    return tickItem.split('T', 2)[1];
  }


  console.log({quality, arrData});
  arrData.forEach((item, i) => {
    if(data.value - quality[i] >= item){
      arrData[i] = item
    }else if(data.value - item > 0){
      arrData[i] = item
    }
  });

  return(

    <BarChart width={750} height={300} data={data} className='chart-wrapper' margin={{
          top: 5, right: 30, left: 20, bottom: 5,
        }}>
  <Tooltip />
      <Bar stackId='pollution' dataKey="value"  fill="#8884d8" dataMax={3}  />
      <Bar stackId='pollution' dataKey="value"  fill="red" />


      <CartesianGrid stroke="#ccc" strokeDasharray="1 1" />
      <XAxis height={100} dataKey="date" interval={0} angle={30} label="Time[h]" tickFormatter={formatXAxis}/>


       <ReferenceLine y={quality.good} label="good" stroke="#dba914" strokeDasharray="3 3" />
       <ReferenceLine y={quality.moderate} label="moderate" stroke="yellow" strokeDasharray="3 3" />
       <ReferenceLine y={quality.sufficient} label="sufficient" stroke="red" strokeDasharray="3 3" />
       <ReferenceLine y={quality.sufficient} label="bad" stroke="#7a0505" strokeDasharray="3 3" />

             <ReferenceArea y1={0} y2={quality.very_good} label="very good" fill="#d6f593" strokeOpacity={0.3} />
      <YAxis dataKey="value" label={{ value: stand.code + '[ug/m3]', angle: -90 }} width={110}/>

      <div>{quality.name}</div>
  </BarChart>


  )
}
