import React, { Component } from 'react';
import '../App.css';
import { Map, Marker, Popup, TileLayer, Tooltip } from 'react-leaflet';
import { Icon, latLngBounds } from 'leaflet';
import axios from 'axios';


class PositionMap extends Component {
  constructor(props){
    super(props);
    this.leafPoint = new Icon({
      iconUrl: 'icons8-fallen-leaf-48.png',
      iconSize: [30, 30]
    });
    this.bounds = latLngBounds(props.stations.map(sta => {return [sta.gegrLat, sta.gegrLon]}))
    axios.get(`/stations`)
    .then((res) => {
      this.allStations = res.data

    })
  }

  handleClick = (e) => {
    this.props.onClickStation(e);
 }

  render(){

let pos = [this.props.stations.map(sta => {return [sta.gegrLat, sta.gegrLon]})]
console.log(this.bounds)

     return(

      <div className='map-wraper'>
      <Map center={this.props.position} zoom={6} bounds={latLngBounds([this.props.position], [50.912475, 15.31219])} >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors"
        />
        {this.props.stations.map(sta => {
        return (
          <Marker key={sta.id} position={[sta.gegrLat, sta.gegrLon]} icon={this.leafPoint} onClick={this.handleClick.bind(this, sta)}>
            <Popup>{sta.name}</Popup>
            <Tooltip>
              {sta.name}
            </Tooltip>
          </Marker>
        )
      })}
      </Map>
      </div>
  )}
}
export default PositionMap;
