import React, {useState, useEffect} from 'react';
import './App.css';
import Nav from './components/Nav';
import PositionMap from './components/PositionMap';
import SearchForm from './components/SearchForm';
import Stations  from './components/Stations';
import axios from 'axios';

function App() {
  const [stationGraph, setStationGraph] = useState([]);
  const [stations, setStations] = useState([]);
  const [position, setPosition] = useState([51.919437, 19.145136])



  const onClickStation = (data)=>{
      setStationGraph([data])
  }

    const getSerchResult = (e) => {
      e.preventDefault();
      const result = e.target.elements.search.value;
        axios.get(`/cities-stations/${result}`)
        .then((res) => {
          setStations(res.data);

        });
    }

  return (
    <div>
        <Nav />
      <div className='app'>
        <PositionMap  position = {position} onClickStation={onClickStation} />
        <div className='data-wraper'>
          <SearchForm getSerchResult = {getSerchResult} />
          <Stations station = {stationGraph} / >
        </div>
      </div>
    </div>

  );
}
export default App;
