import React, { useState, useEffect } from 'react';
import { Stands } from './Stands';

export const Stations = ({stationsName}) =>{

const [active, setActive] = useState([])
let lenthElement = []
let itemList = []

function clickEvent (event, variable){

setActive(Array(lenthElement))
  let Arr = [...active]
  Arr[variable] = Arr[variable] === 'open' ? '' : 'open'
    setActive(Arr);
}

if(stationsName){
  lenthElement = stationsName.lenth
   itemList = stationsName.map((sta, i) => {

    return (
      <div key={sta.id} className='station-card'>
      <button className='accordion' onClick={e => clickEvent(e, i)} >{sta.name}</button>
        <p className={active[i]}>
          <h3>{sta.name}</h3>
          <Stands station={sta}/>
        </p>
      </div>


    )
  })
}
  return(
<div className='station-wraper'>
  {itemList}
</div>
  )
}
