import React, { useState } from 'react';
import axios from 'axios';

import '../App.css';

function Search() {


    const [city, setCity] = useState('')

    const handlerCityChange = (event) => {
      setCity(event.target.value)
    }

    const getStations = async () => {

      //   fetch('/search',{
      //   method: 'POST',
      //   headers: {
      //     'Content-Type': 'application/json'
      //   },
      //   body: JSON.stringify({city})
      // });
      let data =await axios.get('/cities-stations/Warszawa').then( data => {
          setCity(data)
      })


}

    return (

      <div className='form-wraper'>
      <div className='search-form'>
          <input className='search-field' value={city} name='search-field' placeholder='Search' type='text' onChange={handlerCityChange} />
        <button onClick={getStations} >Search</button>
      </div>
      </div>

    )

}

export default Search;
